INSERT INTO `answer` VALUES ('44', '<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">爱人的吻和拥抱。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">陌生人的善意。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">妈妈做的饭。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">和友人畅谈。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">睡醒后穿过窗帘的阳光。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">草原。花田。河流。湖泊。海洋。雪花。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">充满快乐的回忆。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">大笑到肚子疼。</span><br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<br style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\" />\r\n<span style=\"color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 22.1000003814697px;\">初恋。</span>', '54', '1423278153', '12', '0', '3', '888');
